/*==================================================
src/components/Credits.js

I display the credits and let the user add new ones.
==================================================*/
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import AccountBalance from './AccountBalance';

class Credits extends Component {
  render() {
    return (
      <div>
        <h1>Credits</h1>

        {/* I show a link back to the home page */}
        <Link to="/">Home</Link>
        <br /><br />

        {/* I loop through the credits list and show each credit */}
        {this.props.credits.map((credit, index) => (
          <div key={index}>
            <p>Description: {credit.description}</p>
            <p>Amount: ${Number(credit.amount).toFixed(2)}</p>
            <p>Date: {credit.date.slice(0, 10)}</p>
            <hr />
          </div>
        ))}

        <br />

        {/* I show the account balance */}
        <AccountBalance accountBalance={this.props.accountBalance} />
      </div>
    );
  }
}

export default Credits;
