/*==================================================
src/App.js

This is the top-level component of the app.
It contains the top-level state.
==================================================*/
import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';

// Import other components
import Home from './components/Home';
import UserProfile from './components/UserProfile';
import LogIn from './components/Login';
import Credits from './components/Credits';
import Debits from './components/Debits';

class App extends Component {
  constructor() {
    // I create and initialize state here
    super();
    this.state = {
      accountBalance: 1234567.89,
      creditList: [],
      debitList: [],
      currentUser: {
        userName: 'Joe Smith',
        memberSince: '11/22/99',
      }
    };
  }

  // I update the current user's username after login
  mockLogIn = (logInInfo) => {
    const newUser = { ...this.state.currentUser };
    newUser.userName = logInInfo.userName;
    this.setState({ currentUser: newUser });
  }

  // I load credits and debits from the API when the app starts
  async componentDidMount() {
    try {
      // I fetch the credits data
      const creditResponse = await fetch('https://johnnylaicode.github.io/api/credits.json');
      const creditData = await creditResponse.json();

      // I fetch the debits data
      const debitResponse = await fetch('https://johnnylaicode.github.io/api/debits.json');
      const debitData = await debitResponse.json();

      // I store the results in state
      this.setState({
        creditList: creditData,
        debitList: debitData
      });
    } catch (error) {
      console.error("I had an issue loading data:", error);
    }
  }

  // I create the routes and pass props down to the components
  render() {
    const HomeComponent = () => (
      <Home accountBalance={this.state.accountBalance} />
    );

    const UserProfileComponent = () => (
      <UserProfile
        userName={this.state.currentUser.userName}
        memberSince={this.state.currentUser.memberSince}
      />
    );

    const LogInComponent = () => (
      <LogIn
        user={this.state.currentUser}
        mockLogIn={this.mockLogIn}
      />
    );

    const CreditsComponent = () => (
      <Credits credits={this.state.creditList} />
    );

    const DebitsComponent = () => (
      <Debits debits={this.state.debitList} />
    );

    return (
      <Router basename="/bank-of-react-starter-code">
        <div>
          <Route exact path="/" render={HomeComponent} />
          <Route exact path="/userProfile" render={UserProfileComponent} />
          <Route exact path="/login" render={LogInComponent} />
          <Route exact path="/credits" render={CreditsComponent} />
          <Route exact path="/debits" render={DebitsComponent} />
        </div>
      </Router>
    );
  }
}

export default App;
